class Solution{ 
public: 
    bool solvable(vector<int>& v,int diff,int K){ 
      
        int n=v.size(); 
        int prev=0; 
        int removed=0; 
        for(int i=1;i<n&&removed<K;i++){ 
            if(i==n-1 || v[i+1]-v[prev]>diff){ 
                if(v[i]-v[prev]>diff)return false; 
                prev=i; 
            } 
            else removed++; 
        } 
        return (removed==K); 
    } 
     
    int minDiff(vector<int> v,int K){ 
         
        int hi=v.back()-v[0]; 
        int lo=0; 
         
        for(int i=0;i+1<v.size();i++)lo=max(lo,v[i+1]-v[i]); 
         
        while(lo<hi){ 
            int mid=(lo+hi)/2; 
            if(solvable(v,mid,K))hi=mid; 
            else lo=mid+1; 
        } 
        return lo;         
    } 
};

vector<vector<int>> adj; 
vector<int> vis; 
void solve() { 
  int n; 
  cin >> n; 
  adj.resize(n); 
  for(int i = 0 ; i < n ; i++){ 
    int x;cin >> x; 
    int N = i + x; 
    int P = i - x; 
    if(N < n) 
      adj[N].push_back(i); 
    if(P >= 0) 
      adj[P].push_back(i); 
  } 
  vis.resize(n, -1); 
  queue<int> Q; 
  Q.push(n-1); 
  vis[n-1] = 0; 
  while(!Q.empty()){ 
    int u = Q.front(); 
    Q.pop(); 
    for(int v : adj[u]){ 
      if(vis[v] == -1){ 
        Q.push(v); 
        vis[v] = 1 + vis[u]; 
      } 
    } 
  } 
  for(int i = 0; i < n ; i++) 
    cout << vis[i] <<endl; 
}
