#include <bits/stdc++.h>

using namespace std;

int getMinimumOperations(vector<int> executionTime, int x, int y) {
    int n = executionTime.size();
    long long left = 1, right = 1;
    for(auto &t : executionTime){
        right = max(right, (long long)t);
    }
    right = 1e12;
    auto isPossible = [&](long long k) -> bool {
        long long total = 0;
        long long diff = x - y;
        for(auto &t : executionTime){
            if(t > k * y){
                long long required = t - k * y;
                long long m = (required + diff - 1) / diff;
                total += m;
                if(total > k) return false;
            }
        }
        return total <= k;
    };
    while(left < right){
        long long mid = left + (right - left) / 2;
        if(isPossible(mid)){
            right = mid;
        }
        else{
            left = mid + 1;
        }
    }
    return left;
}

int main(){
    int n;
    cin >> n;
    vector<int> executionTime(n);
    for(auto &x : executionTime) cin >> x;
    int x, y;
    cin >> x >> y;
    cout << getMinimumOperations(executionTime, x, y) << endl;
}

#include <bits/stdc++.h>
using namespace std;

int findConsistentLogs(vector<int> userEvent) {
    int n = userEvent.size();
    unordered_map<int, int> freqMap;
    for(auto user : userEvent){
        freqMap[user]++;
    }
    int min_freq = INT32_MAX;
    for(auto &[k, v] : freqMap){
        min_freq = min(min_freq, v);
    }
    vector<int> uniqueUsers;
    for(auto &[k, v] : freqMap){
        uniqueUsers.push_back(k);
    }
    int m = uniqueUsers.size();
    unordered_map<int, int> userToIndex;
    for(int i=0;i<m;i++) userToIndex[uniqueUsers[i]] = i;
    vector<int> counts(m, 0);
    int left = 0, max_len = 0, unique_in_window = 0, num_with_min_freq = 0;
    for(int right=0; right<n; right++){
        int user = userEvent[right];
        int idx = userToIndex[user];
        counts[idx]++;
        if(counts[idx] == 1){
            unique_in_window++;
        }
        if(counts[idx] == min_freq){
            num_with_min_freq++;
        }
        if(counts[idx] == min_freq + 1){
            num_with_min_freq--;
        }
        while(counts[idx] > min_freq){
            int left_user = userEvent[left];
            int left_idx = userToIndex[left_user];
            if(counts[left_idx] == min_freq){
                num_with_min_freq--;
            }
            counts[left_idx]--;
            if(counts[left_idx] == min_freq){
                num_with_min_freq++;
            }
            if(counts[left_idx] == 0){
                unique_in_window--;
            }
            left++;
        }
        if(num_with_min_freq >=1){
            max_len = max(max_len, right - left +1);
        }
    }
    return max_len;
}

int main()
{
    int n;
    cin >> n;
    vector<int> userEvent(n);
    for(auto &x : userEvent) cin >> x;
    cout << findConsistentLogs(userEvent);
}
