import java.util.*;
import java.io.*;
import java.lang.Math;

public class Main {
    public static int getMinSum(int N,int K,int[] A)
    {
        int mod = 1000000007;
        ArrayList<Long> subarraySums = new ArrayList<>();
        for(int i = 0; i < N; i++) {
            long sum = 0;
            for(int j = i; j < N; j++) {
                sum += A[j];
                subarraySums.add(sum);
            }
        }
        Collections.sort(subarraySums);
        long result = 0;
        for(int i = 0; i < K; i++) {
            result = (result + subarraySums.get(i)) % mod;
        }
        return (int)result;
    }
    public static void main(String[]args)
    {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        int K = sc.nextInt();
        int[] A = new int[N];
        for(int i = 0; i < N; i++)
        {
            A[i] = sc.nextInt();
        }
        sc.close();
        System.out.print(getMinSum(N,K,A));
    }
}
